package ths.project.service.common.vo.drowDown;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;/** * @author:Toledo * @date:2021/3/22 10:55 * @descripthion: **/@ApiModel(value = "下拉菜单vo")@Datapublic class DropDownVo extends DropDownBaseVo {    @ApiModelProperty(value = "下拉选项对应的code或值")    private String code;    @ApiModelProperty("下拉菜单名称")    private String name;    @ApiModelProperty("唯一id")    private String pkid;    public DropDownVo code(String code) {        this.code = code;        return this;    }    public DropDownVo name(String name) {        this.setName(name);        return this;    }    public static DropDownVo build() {        return new DropDownVo();    }    public DropDownVo() {    }    public DropDownVo(String code, String name) {        this.code = code;        this.setName(name);    }}