mapper.select:
SELECT max([MODELTIME]) MAX_DATE FROM
[AIR_FORECAST].[T_BAS_MODELWQ_DAY_ROW]


        AND MODEL = #{model}


        AND POINTCODE = #{pointCode}



            AND MODELTIME >=  CONVERT(datetime ,#{month}+'-01 00:00:00',120)
            AND MODELTIME < dateadd(month,1,CONVERT(datetime,#{month}+'-01 00:00:00',120))

SELECT
    CONVERT (varchar(13), MODELTIME,120) AS MODELTIME,
    CONVERT (varchar(10), RESULTTIME,120) AS RESULTTIME,
    PM25,
    O3_8
FROM
    AIR_FORECAST.T_BAS_MODELWQ_DAY_ROW
WHERE


              STEP BETWEEN 1 AND ${step}


              STEP BETWEEN 1 AND 14



        AND MODEL = #{model}


        AND RESULTTIME >= #{startTime}
        AND MODELTIME<#{startTime}


        AND RESULTTIME <= #{endTime}



            AND CONVERT ( VARCHAR ( 100 ), MODELTIME, 24 ) = '00:00:00'


            AND CONVERT ( VARCHAR ( 100 ), MODELTIME, 24 ) = '12:00:00'



        AND POINTCODE = #{pointCode}

    ORDER BY
    MODELTIME ASC,RESULTTIME ASC

SELECT B.MODELNAME, CONVERT (varchar(20), MODELTIME,120) AS MODELTIME, CONVERT (varchar(10), RESULTTIME,120) AS RESULTTIME, isnull(POINTNAME,'--') AS POINTNAME, isnull(CAST(PM25 AS VARCHAR),'--') AS PM25, isnull(CAST(O3_8 AS VARCHAR),'--') AS O3_8, isnull(CAST(PM10 AS VARCHAR),'--') AS PM10, isnull(CAST(CO AS VARCHAR),'--') AS CO, isnull(CAST(O3 AS VARCHAR),'--') AS O3, isnull(CAST(SO2 AS VARCHAR),'--') AS SO2, isnull(CAST(NO2 AS VARCHAR),'--') AS NO2, isnull(CAST(AQI_REANGE AS VARCHAR),'--') AS AQI_REANGE, isnull(CAST(AQILEVELREANGESTATE AS VARCHAR),'--') AS AQILEVELREANGESTATE, isnull(CAST(PRIMPOLLUTE AS VARCHAR),'--') AS PRIMPOLLUTE FROM AIR_FORECAST.T_BAS_MODELWQ_DAY_ROW A INNER JOIN AIR_FORECAST.T_BAS_MODELS B ON A.MODEL = B.MODEL WHERE
A.STEP BETWEEN 1 AND ${step}
A.STEP BETWEEN 1 AND 14
AND A.MODEL = #{model}
AND RESULTTIME >= #{startTime} AND MODELTIME<#{startTime}
AND RESULTTIME <= #{endTime}
AND CONVERT ( VARCHAR ( 100 ), MODELTIME, 24 ) = '00:00:00'
AND modelTime = #{modelTime} + ' 00:00:00' AND RESULTTIME > #{modelTime}+' 00:00:00' AND RESULTTIME <= dateadd(day,14,CONVERT(datetime,#{modelTime}+' 00:00:00',120))
AND CONVERT ( VARCHAR ( 100 ), MODELTIME, 24 ) = '12:00:00'
AND modelTime = #{modelTime} + ' 12:00:00' AND RESULTTIME > #{modelTime}+' 00:00:00' AND RESULTTIME <= dateadd(day,14,CONVERT(datetime,#{modelTime}+' 00:00:00',120))
AND POINTCODE = #{pointCode}
ORDER BY MODELTIME ASC,RESULTTIME ASC

SELECT max(RESULTTIME) END_DATE,dateadd(day,-14,max(RESULTTIME)) AS START_DATE FROM
AIR_FORECAST.T_BAS_MODELWQ_DAY_ROW


        AND MODEL = #{model}


        AND POINTCODE = #{pointCode}
