SELECT ROW_NUMBER() OVER(ORDER BY T1.EVERY_TIME) ROWNU, LEFT(T1.EVERY_TIME,16) AS MONDATE, CASE WHEN T2.AQI IS NULL THEN '-' WHEN T2.AQI=0 THEN '-' ELSE T2.AQI END AS AQI, CASE WHEN T2.PM25 IS NULL THEN '-' ELSE T2.PM25 END AS PM25, CASE WHEN T2.PM10 IS NULL THEN '-' ELSE T2.PM10 END AS PM10, CASE WHEN T2.O3 IS NULL THEN '-' ELSE T2.O3 END AS O3, CASE WHEN T2.SO2 IS NULL THEN '-' ELSE T2.SO2 END AS SO2, CASE WHEN T2.NO2 IS NULL THEN '-' ELSE T2.NO2 END AS NO2, CASE WHEN T2.CO IS NULL THEN '-' ELSE T2.CO END AS CO, T1.REGIONNAME AS SNAME FROM ( SELECT CONVERT(VARCHAR (100), DATEADD(hour, NUMBER, CAST(#{beginDate}+':00:00' AS DATETIME)),20) AS EVERY_TIME,M.REGIONNAME FROM MASTER..SPT_VALUES N,( SELECT SNAME AS REGIONNAME FROM TENVAIR.T_CONFIG_TYPE_REGION_INFO WHERE TYPECODE IN('6','7','8','9','99') ) M WHERE N.TYPE = 'P' AND DATEADD(hour, NUMBER, CAST(#{beginDate}+':00:00' AS DATETIME)) <= CAST(#{endDate}+':00:00' AS DATETIME) )T1 LEFT JOIN( SELECT POINTNAME AS SNAME, CONVERT(VARCHAR(20),AQI) AS AQI, CONVERT(VARCHAR(20),PM2_5) AS PM25, CONVERT(VARCHAR(20),PM10 ) AS PM10, CONVERT(VARCHAR(20),SO2 ) AS SO2, CONVERT(VARCHAR(20),NO2 ) AS NO2, CONVERT(VARCHAR(20),CONVERT(DECIMAL(3,1),CO)) AS CO, CONVERT(VARCHAR(20),O3 ) AS O3, CONVERT(VARCHAR(20),MONITORTIME,20) as MONDATE FROM TENVAIR.T_ENV_AIRDATA_POINT_HOUR WHERE MONITORTIME BETWEEN #{beginDate}+':00:00' AND #{endDate}+':00:00' )T2 ON T1.EVERY_TIME = T2.MONDATE AND T1.REGIONNAME = T2.SNAME WHERE T1.REGIONNAME IN
#{SNAMES}
ORDER BY T1.EVERY_TIME,T1.REGIONNAME

SELECT SNAME AS NAME FROM( SELECT * FROM TENVAIR.T_ENV_AUTOMONI_AIRSTATIONINFO
WHERE REGIONNAME IN #{REGIONNAME}
)T1 INNER JOIN ( SELECT * FROM TENVAIR.T_CONFIG_TYPE_REGION_INFO
WHERE TYPECODE IN #{TYPE}
WHERE TYPECODE IN ('6','7','8')
) T2 ON T1.POINTCODE = T2.SCODE
